//A further example of inheritancepublic class Word {	private String ch;	public Word() {		ch = " ";	}		public Word(String s) {		ch = s;	}		public String getWord() {		return ch;	}		public void setWord(String s) {		ch = s;	}		public String toString() {		return ch;	}}public class Noun extends Word {	private boolean plural;		public Noun(String s, String p) {		super(s);		if (p.equals("singular"))			plural = false;		else			plural = true;	}		public String toString() {		String s = getWord();		if (plural) s += "s are";		else s += " is";		return s;	}}public class Verb extends Word {		public Verb(String s) {		super(s);	}		public String toString() {		String v = getWord();		return " " + v + v.substring(v.length() - 1, v.length()) + "ing ";	}}public class Adjective extends Word {		public Adjective(String s) {		super(s);	}		public String toString() {		return getWord() + " ";	}}